INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105332, 'Ritonavir', 'Modafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme. Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4. The clinical significance of this potential interaction is unknown. Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105453/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.[3] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[4] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[5] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105333, 'Ritonavir', 'Nelfinavir', 'Moderate', 'Coadministration with ritonavir may significantly increase the plasma concentrations of nelfinavir. The mechanism is ritonavir inhibition of CYP450 3A4 metabolism of nelfinavir.', NULL, 'Alteration in dosages or regimen is recommended by the manufacturer of nelfinavir during concomitant therapy with ritonavir.', NULL, 'Alteration in dosages or regimen is recommended by the manufacturer of nelfinavir during concomitant therapy with ritonavir. However, appropriate dosages for the combination, with respect to safety and efficacy, have not been established. Patients receiving the combination should be closely monitored for toxicity such as elevations in liver function tests and undue gastrointestinal disturbances, and the dosage(s) adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105454/', '[1] Kempf DJ, Marsh KC, Kumar G, et al "Pharmacokinetic enhancement of inhibitors of the human immunodeficiency virus protease by coadministration with ritonavir." Antimicrob Agents Chemother 41 (1997): 654-60[2] Lu JF, Blaschke TF, Flexner C, Rosenkranz SL, Sheiner LB "Model-based Analysis of the Pharmacokinetic Interactions Between Ritonavir, Nelfinavir, and Saquinavir after Simultaneous and Staggered Oral Administration." Drug Metab Dispos 30 (2002): 1455-61[3] Kempf D, Marsh K, Denissen J, Kumar G, Rodrigues D, McDonald E, Flentge C, Green B, Chen X, Leonard J, Norbeck D "Coadministration with ritonavir enhances the plasma levels of HIV protease inhibitors by inhibition of cytochrome P450." 3rd Conf Retro and Opportun Infect (199