INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105335, 'Paritaprevir', 'Omeprazole', 'Moderate', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may decrease the plasma concentrations of omeprazole. The proposed mechanism of interaction is CYP450 2C19 induction by ritonavir.', NULL, 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir.', NULL, 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir. The dosage of omeprazole may be increased in patients whose symptoms are not well controlled; however, some authorities suggest that the dosage of omeprazole should be limited to 40 mg/day.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105456/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[5] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[6] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105336, 'Pitolisant', 'Paritaprevir', 'Moderate', 'Coadministration with pitolisant may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Pitolisant is a borderline/weak inducer of CYP450 3A4.', NULL, 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates.', NULL, 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates. Clinical and laboratory monitoring may be appropriate whenever pitolisant is added to or withdrawn from therapy, and dosage adjustments made if necessary. It may be advisable to avoid concomitant use of pitolisant and CYP450 3A4 substrates with a narrow therapeutic index, if possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105458/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105337, 'Posaconazole', 'Paritaprevir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of paritaprevir, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if paritaprevir is prescribed in combination with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised if paritaprevir is prescribed in combination with potent CYP450 3A4 inhibitors. Patients should be monitored for signs and symptoms of hepatotoxicity (i.e., ALT and bilirubin elevations).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105459/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[3] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105338, 'Pravastatin', 'Paritaprevir', 'Major', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may significantly increase the plasma concentrations of pravastatin. The mechanism may involve inhibition of OATP1B1-mediated hepatic uptake of pravastatin by both paritaprevir and ritonavir.', NULL, 'The benefits of using pravastatin with ombitasvir/paritaprevir/ritonavir plus dasabuvir should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', NULL, 'The benefits of using pravastatin with ombitasvir/paritaprevir/ritonavir plus dasabuvir should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis. If coadministration is required, the lowest effective dosage of pravastatin should be used and close monitoring for musculoskeletal toxicity is recommended. The dosage of pravastatin should be limited to 40 mg/day. Alternatively, an interruption of pravastatin may be considered during treatment with ombitasvir/paritaprevir/ritonavir plus dasabuvir, or a different HMG-CoA reductase inhibitor such as fluvastatin or pitavastatin may be substituted. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105460/', '[1] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[2] Fichtenbaum CJ, Gerber JG, Rosenkranz SL, et al. "Pharmacokinetic interactions between protease inhibitors and statins in HIV seronegative volunteers: ACTG Study A5047." AIDS 16 (2002): 569-577[3] Everett DW, Chando TJ, Didonato GC, Singhvi SM, Pan HY, Weinstein SH "Biotransformation of pravastatin sodium in humans." Drug Metab Dispos 19 (1991): 740-8[4] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[5] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[6] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105339, 'Quinestrol', 'Paritaprevir', 'Major', 'Theoretical concerns exist that coadministration of estrogen-containing products may increase the risk of liver enzyme elevations associated with the use of ombitasvir/paritaprevir/ritonavir, with or without dasabuvir. The proposed mechanism is the inhibition of UDP-glucuronosyltransferase (UGT) by ombitasvir, paritaprevir, and dasabuvir.', NULL, 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.', NULL, 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir. Liver function tests should be performed on all patients during the first 4 weeks of treatment and as clinically indicated thereafter. If ALT is elevated above baseline at any time during treatment, the test should be repeated and monitored closely. Therapy should be discontinued if ALT levels remain persistently greater than 10 times the ULN, or if ALT elevations are accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105461/', '[1] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[4] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[5] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105340, 'Ritonavir', 'Relugolix', 'Major', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of relugolix, particularly when the inhibitors are given orally. Relugolix is a substrate for intestinal P-gp.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends taking relugolix first and separating the dosing by at least 6 hours. Patients should be monitored more frequently for adverse effects. Alternatively, treatment with relugolix may be interrupted for up to two weeks if a short course of treatment with a P-gp inhibitor is necessary.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105462/', '[1] "Product Information. Orgovyx (relugolix)." Myovant Sciences, Inc., Brisbane, CA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105341, 'Rilpivirine', 'Paritaprevir', 'Major', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may significantly increase the plasma concentrations of rilpivirine. The mechanism of interaction has not been established, but may involve inhibition of CYP450 3A4-mediated metabolism by ritonavir and paritaprevir. Rilpivirine had minor to no effects on the pharmacokinetics of ombitasvir, paritaprevir, ritonavir, and dasabuvir.', NULL, 'Concomitant use of rilpivirine with ombitasvir/paritaprevir/ritonavir plus dasabuvir is not recommended.', NULL, 'Concomitant use of rilpivirine with ombitasvir/paritaprevir/ritonavir plus dasabuvir is not recommended.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105463/', '[1] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Edurant (rilpivirine)." Tibotec Pharmaceuticals, Titusville, NJ.[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[5] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105342, 'Rosuvastatin', 'Ritonavir', 'Major', 'Coadministration with protease inhibitors may significantly increase the plasma concentrations of rosuvastatin. The mechanism may involve inhibition of OATP1B1-mediated hepatic uptake and/or BCRP-mediated intestinal and hepatobiliary efflux. High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', NULL, 'The benefits of using rosuvastatin with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', NULL, 'The benefits of using rosuvastatin with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis. If coadministration is required, rosuvastatin should be initiated at the lowest effective dosage and not exceed 10 mg per day when prescribed with lopinavir-ritonavir, atazanavir-ritonavir, atazanavir-cobicistat, or ombitasvir/paritaprevir/ritonavir plus dasabuvir. The dosage of rosuvastatin should be limited to 20 mg once a day when used with darunavir-cobicistat. Alternatively, a different HMG-CoA reductase inhibitor such as fluvastatin may be considered. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism, Excretion', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105464/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[3] Neuvonen PJ, Niemi M, Backman JT "Drug interactions with lipid-lowering drugs: Mechanisms and clinical relevance." Clin Pharmacol Ther 80 (2006): 565-81[4] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[5] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb, Princeton, NJ.[6] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[7] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.[8] "Product Information. Crestor (rosuvastatin)." AstraZeneca Pharma Inc, Mississauga, ON.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[11] Hirano M, Maeda K, Shitara Y, Sugiyama Y "Drug-drug interaction between pitavastatin and various drugs via OATP1B1." Drug Metab Dispos 34 (2006): 1229-36[12] Cerner Multum, Inc. "Australian Product Information." O 0[13] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[14] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[15] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[16] Demarles D, Gillotin C, Bonaventure-Paci S, Vincent I, Fosse S, Taburet AM "Single-dose pharmacokinetics of amprenavir coadministered with grapefruit juice." Antimicrob Agents Chemother 46 (2002): 1589-1590[17] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105343, 'Saquinavir', 'Ritonavir', 'Moderate', 'Coadministration with ritonavir (RTV) may significantly increase the bioavailability of saquinavir (SQV) from both the hard gelatin capsule (HGC) and soft gelatin capsule (SGC) formulations. The mechanism is RTV inhibition of CYP450 3A4 metabolism of SQV in the intestine and liver. SQV had negligible effect on the pharmacokinetics of RTV.', NULL, 'Based on the magnitude of interaction, SQV dosage should be reduced when coadministered with RTV.', NULL, 'Based on the magnitude of interaction, SQV dosage should be reduced when coadministered with RTV. A regimen of (or SGC:RTV) 400:400 mg or 1000:100 mg twice daily is usually recommended based on their convenience and favorable safety-to-efficacy profile. Limited data suggest that dosages of 1200 to 2000:100 mg once daily may also be feasible and warrant further investigation. Patients receiving the combination should be closely monitored for toxicity including elevations in liver function tests and neutropenia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105465/', '[1] Angel JB, Kumar A, Parato K, Filion LG, DiazMitoma F, Daftarian P, Pham B, Sun E, Leonard JM, Cameron DW "Improvement in cell-mediated immune function during potent anti-human immunodeficiency virus therapy with ritonavir plus saquinavir." J Infect Dis 177 (1998): 898-904[2] Cameron DW, Japour AJ, Xu Y, Hsu A, Mellors J, Farthing C, Cohen C, Poretz D, Markowitz M, Follansbee S, Angel JB, McMahon D "Ritonavir and saquinavir combination therapy for the treatment of HIV infection." AIDS 13 (1999): 213-24[3] Fitzsimmons ME, C