INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105358, 'Boceprevir', 'Rifabutin', 'Major', 'Coadministration of rifabutin with boceprevir or telaprevir is expected to increase the plasma concentrations of rifabutin and its metabolite and decrease the plasma concentrations of the hepatitis C virus NS3/4A protease inhibitor. The proposed mechanism involves inhibition of rifabutin metabolism via CYP450 3A4 and, conversely, induction of boceprevir and telaprevir metabolism via the same isoenzyme.', NULL, 'Safe and effective dosages have not been established for the concomitant use of rifabutin with either boceprevir or telaprevir.', NULL, 'Safe and effective dosages have not been established for the concomitant use of rifabutin with either boceprevir or telaprevir. Coadministration is not recommended.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105481/', '[1] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[2] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[3] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767363023);
