INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105420, 'Rifabutin', 'Ixabepilone', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ixabepilone, which is primarily metabolized by the isoenzyme. Subtherapeutic levels of ixabepilone may result.', NULL, 'The use of ixabepilone in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'The use of ixabepilone in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with ixabepilone is unknown. Whenever possible, therapeutic agents with low enzyme induction potential should be considered in patients treated with ixabepilone.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105543/', '[1] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105421, 'Rifabutin', 'Ixazomib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ixazomib, which is a substrate of the isoenzyme. When ixazomib was administered with the potent CYP450 3A4 inducer rifampin, ixazomib peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by 54% and 74%, respectively.', NULL, 'Concomitant use of ixazomib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of ixazomib with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105544/', '[1] "Product Information. Ninlaro (ixazomib)." Takeda Pharmaceuticals America, Lincolnshire, IL.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Ninlaro (ixazomib)." Takeda Pharmaceuticals America, Lincolnshire, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105422, 'Omeprazole', 'Kanamycin', 'Moderate', 'Chronic use of proton pump inhibitors (PPIs) may induce hypomagnesemia, and the risk may be increased during concomitant use of diuretics or other agents that can cause magnesium loss. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms. Hypomagnesemia can also cause impaired parathyroid hormone secretion, which may lead to hypocalcemia. In approximately 25% of the cases of PPI-associated hypomagnesemia reviewed by the FDA, the condition did not resolve with magnesium supplementation alone but also required discontinuation of the PPI.', NULL, 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, and tacrolimus.', NULL, 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, and tacrolimus. Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of 