INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105456, 'Rifabutin', 'Nelfinavir', 'Major', 'Coadministration of nelfinavir and rifabutin may result in increased plasma concentrations of rifabutin and decreased plasma concentrations of nelfinavir. The mechanism involves nelfinavir inhibition of rifabutin metabolism via CYP450 3A4 and, conversely, rifabutin induction of nelfinavir metabolism via the same isoenzyme.', NULL, 'To minimize the risk of rifabutin toxicity including leukopenia, uveitis, arthralgias and skin discoloration, nelfinavir labeling recommends that rifabutin be administered at half the standard dosage in patients treated with nelfinavir.', NULL, 'To minimize the risk of rifabutin toxicity including leukopenia, uveitis, arthralgias and skin discoloration, nelfinavir labeling recommends that rifabutin be administered at half the standard dosage in patients treated with nelfinavir. Some experts suggest decreasing rifabutin dose from 300 to 150 mg if given daily but administering the full 300 mg dose during intermittent therapy (i.e. twice- or three-times-weekly directly observed therapy). Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, nelfinavir labeling also recommends using the 1250 mg twice a day dosage during coadministration with rifabutin. Alternatively, some experts recommend increasing the dose of nelfinavir from 750 to 1000 mg if the three-times-a-day dosing is used.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105579/', '[1] Gariano RF, Gooney EL "Uveitis following administration of the protease inhibitor indinavir to a pa