    ) VALUES (1387028, 'Irinotecan', 'Chlorothiazide', 'Moderate', 'Irinotecan-induced vomiting or diarrhea may increase the risk and/or severity of dehydration produced by diuretics.', NULL, 'Concomitant use of diuretics during irinotecan administration or during periods of vomiting or diarrhea that may follow is not recommended.', NULL, 'Concomitant use of diuretics during irinotecan administration or during periods of vomiting or diarrhea that may follow is not recommended.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
