    ) VALUES (1387184, 'Chlorothiazide', 'Tolterodine', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of thiazide diuretics. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
