INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105543, 'Tipranavir', 'Omeprazole', 'Moderate', 'The concomitant administration of tipranavir-ritonavir with omeprazole or esomeprazole may significantly decrease systemic exposure to the proton pump inhibitors. The proposed mechanism is induction of CYP450 2C19 metabolism by tipranavir-ritonavir.', NULL, 'If this combination is given concurrently, patients should be monitored for altered clinical efficacy of the proton pump inhibitor.', NULL, 'If this combination is given concurrently, patients should be monitored for altered clinical efficacy of the proton pump inhibitor. Dose adjustments may be necessary; however, specific dosage recommendations are not available.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105666/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[7] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[8] Cerner Multum, Inc. "Australian Product Information." O 0[9] C