INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105635, 'Thallous Chloride', 'Sodium bicarbonate', 'Moderate', 'Coadministration of certain drugs may alter the myocardial uptake of thallous chloride Tl-201 and interfere with results of myocardial imaging studies. Drugs that increase or decrease coronary blood flow or potassium uptake may alter the biodistribution of thallous chloride Tl-201.', NULL, 'Clinicians should be aware of possible diagnostic interference by these drugs.', NULL, 'Clinicians should be aware of possible diagnostic interference by these drugs.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105758/', '[1] "Product Information. Thallous Chloride Tl 201 (thallous chloride Tl-201)." GE Healthcare, Princeton, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Nuclear Pharmacy Website. University of Arkansas for Medical Sciences. College of Pharmacy "Drug - Radiopharmaceutical Interactions. Available from: URL: http://nuclearpharmacy.uams.edu/nicki.html#tl." ([cited Feb2015]):', 1767365206);
