INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105642, 'Trandolapril', 'Sodium bicarbonate', 'Minor', 'Coadministration with antacids may decrease the oral bioavailability of captopril and other angiotensin converting enzyme (ACE) inhibitors due to delayed gastric emptying and/or elevated gastric pH. Based on available data, the clinical significance of this interaction appears to be minor. As a precaution, patients may want to consider separating the administration times of ACE inhibitors and antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) by 1 to 2 hours.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105765/', '[1] Mantyla R, Mannisto PT, Vuorela A, Sundberg S, Ottoila P "Impairment of captopril bioavailability by concomitant food and antacid intake." Int J Clin Pharmacol Ther Toxicol 22 (1984): 626-9[2] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[3] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[4] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105643, 'Vandetanib', 'Omeprazole', 'Minor', 'The use of vandetanib with proton pump inhibitors may theoretically reduce its gastrointestinal absorption. Vandetanib exhibits pH-dependent solubility, with increased solubility at lower pH. The clinical significance is unknown. Dose adjustments are not required when vandetanib is administered with omeprazole or ranitidine.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105766/', '[1] "Product Information. Vandetanib (vandetanib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365206);
