INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106023, 'Methotrexate', 'Levomefolic acid', 'Moderate', 'Coadministration of methotrexate with vitamin preparations containing folic acid, folinic acid (leucovorin) or their derivatives may diminish the therapeutic effects of methotrexate. The proposed mechanism involves pharmacodynamic antagonism and/or competitive inhibition at the receptor site.', NULL, 'Although folic acid or folinic acid are frequently used along with methotrexate to reduce adverse effects or as an antidote to high dose methotrexate therapy, patients receiving methotrexate should be advised to avoid the inadvertent use of vitamin preparations containing folic acid or its derivatives.', NULL, 'Although folic acid or folinic acid are frequently used along with methotrexate to reduce adverse effects or as an antidote to high dose methotrexate therapy, patients receiving methotrexate should be advised to avoid the inadvertent use of vitamin preparations containing folic acid or its derivatives.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106146/', '[1] "Product Information. Methotrexate Sodium (methotrexate)." Teva Pharmaceuticals (formerly IVAX), Miami, FL.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Nesher G, Mates M, Zevin S "Effect of caffeine consumption on efficacy of methotrexate in rheumatoid arthritis." Arthritis Rheum 48 (2003): 571-572', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106024, 'Methotrexate', 'Labetalol', 'Moderate', 'Coadministration of methotrexate with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Methotrexate, especially at higher doses or with prolonged treatment, has been associated with hepatotoxicity including acute hepatitis, chronic fibrosis, necrosis, cirrhosis, and liver enzyme elevations.', NULL, 'The risk of hepatic injury should be considered when methotrexate is used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when methotrexate is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Baseline and periodic monitoring of hepatic function is recommended, while liver biopsy may be warranted during long-term use of methotrexate. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106147/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Methotrexate (methotrexate)." Lederle Laboratories, Wayne, NJ.[3] Nesher G, Mates M, Zevin S "Effect of caffeine consumption on efficacy of methotrexate in rheumatoid arthritis." Arthritis Rheum 48 (2003): 571-572', 1767365206);
