INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106313, 'Glycerol phenylbutyrate', 'Oxycodone', 'Moderate', 'Coadministration of glycerol phenylbutyrate may lead to increased concentrations of CYP450 2D6 substrates. The proposed mechanism is inhibition of CYP450 2D6 by phenylbutyrate, the active moiety of glycerol phenylbutyrate, which has been shown to be an in vitro inhibitor of this isoenzyme.', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol).', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106436/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc, South San Francisco, CA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106314, 'Isoniazid', 'Oxycodone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of oxycodone, which is primarily metabolized by the isoenzyme. Increased oxycodone concentrations could increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Itraconazole, telithromycin and voriconazole are considered potent inhibitors of CYP450 3A4.', NULL, 'Caution may be advisable when oxycodone is prescribed with weak CYP450 3A4 inhibitors.', NULL, 'Caution may be advisable when oxycodone is prescribed with weak CYP450 3A4 inhibitors. The risk of overdose may be increased following the initiation of a CYP450 3A4 inhibitor in patients already receiving oxycodone. Patients should be closely monitored for signs and symptoms of sedation, respiratory depression, and hypotension. Following discontinuation of the CYP450 3A4 inhibitor, patients should be monitored for reduced efficacy of oxycodone or development of withdrawal symptoms due to reduced plasma oxycodone levels.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106437/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company, Norwalk, CT.[4] Nieminen TH, Hagelberg NM, Saari TI, et al "Grapefruit juice enhances the exposure to oral oxycodone." Basic Clin Pharmacol Toxicol 107 (2010): 782-8', 1767365206);
