INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106369, 'Fosphenytoin', 'Sodium oxybate', 'Major', 'The central nervous system and respiratory depressant effects of sodium oxybate, which is the sodium salt of gamma hydroxybutyrate (GHB), may be potentiated by concomitant use of other agents with CNS depressant effects including anticonvulsants. Animal data suggest a possible pharmacokinetic interaction between sodium oxybate and certain anticonvulsants like valproate, phenytoin, ethosuximide, and trimethadione that can inhibit the enzyme GHB dehydrogenase, which converts GHB to succinic semialdehyde.', NULL, 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.', NULL, 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible. Otherwise, close monitoring and/or dosage reductions should be considered. For patients already stabilized on sodium oxybate who are prescribed divalproex sodium, the manufacturer recommends that the nightly dosage of sodium oxybate be reduced initially by at least 20%. Conversely, for patients already taking divalproex sodium, it is recommended that prescribers use a lower starting dosage of sodium oxybate upon initiation of treatment. Further dosage adjustments should be made according to patient response and tolerance. No specific dosage recommendations are available for use with other anticonvulsants.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106492/', '[1] "Product Information. Xyrem (sodium oxybate)." Orphan Medical, Minnetonka, MN.[2] "Product Information. Xywav (calcium/magnesium/potass/sodium oxybates).