INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1387312, 'Fluconazole', 'Chlorpropamide', 'Major', 'Coadministration with inhibitors of CYP450 2C9 including certain azole antifungal agents such as fluconazole, miconazole, and voriconazole may increase the plasma concentrations of sulfonylureas, many of which have been found to be substrates of the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when sulfonylureas are used concomitantly with azole antifungal agents that inhibit CYP450 2C9. Blood glucose should be closely monitored, and the sulfonylurea dosage adjusted as necessary. Patients should also be apprised of the increased risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately, discontinue the azole antifungal agent, and contact their physician.', 'Metabolism', 'Caution is advised when sulfonylureas are used concomitantly with azole antifungal agents that inhibit CYP450 2C9.', NULL, NULL, 'Major', 'pharmacodynamic');
