INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1387322, 'Gatifloxacin', 'Chlorpropamide', 'Major', 'Gatifloxacin may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of various quinolones has been associated with disturbances in blood glucose homeostasis possibly stemming from effects on pancreatic beta cell ATP-sensitive potassium channels that regulate insulin secretion.', 'DDInter', 1767317475, 'The use of gatifloxacin is contraindicated in patients with diabetes mellitus. Other quinolones may be safer alternatives in such patients, although all quinolones should be used with caution. Blood glucose should be closely monitored whenever quinolones are prescribed to patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during quinolone therapy, patients should initiate appropriate remedial therapy immediately, discontinue the antibiotic, and contact their physician.', 'Antagonism', 'The use of gatifloxacin is contraindicated in patients with diabetes mellitus.', NULL, NULL, 'Major', 'pharmacodynamic');
