INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1387357, 'Itraconazole', 'Chlorpropamide', 'Minor', 'Itraconazole, a potent inhibitor of CYP450 3A4, does not appear to significantly affect the pharmacokinetics of available oral hypoglycemic agents, most of which are primarily metabolized by CYP450 2C8 (pioglitazone, rosiglitazone, repaglinide) or 2C9 (nateglinide, sulfonylureas). In most patients, no change in antidiabetic effect was reported. No specific precautions should be necessary during coadministration of most oral hypoglycemic agents with itraconazole. However, dosage adjustments may be necessary if an interaction is suspected.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1387358, 'Ivacaftor', 'Chlorpropamide', 'Moderate', 'Coadministration with ivacaftor may increase the plasma concentrations of drugs that are substrates of the CYP450 2C9 isoenzyme. The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of CYP450 2C9 by ivacaftor.', 'DDInter', 1767317475, 'Caution is advised when ivacaftor is used concurrently with drugs that are known CYP450 2C9 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when ivacaftor is used concurrently with drugs that are known CYP450 2C9 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
