INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1387649, 'Garlic', 'Chlorthalidone', 'Minor', 'Garlic has been found in some studies to lower blood pressure and may theoretically potentiate the effects of hypotensive agents. A meta-analysis of 11 randomized, placebo-controlled studies showed a mean decrease of 4.6 mmHg for systolic blood pressure and no significant effect for diastolic blood pressure in subjects treated with garlic (most often garlic powder 600 to 900 mg/day, providing 3.6 to 5.4 mg of the active component allicin) compared to placebo.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1387650, 'Ginkgo biloba', 'Chlorthalidone', 'Moderate', 'An isolated case report describes an elderly patient with hypertension whose blood pressure increased following the addition of ginkgo while receiving a thiazide diuretic. The mechanism of interaction is unknown.', 'DDInter', 1767317475, 'In general, patients should consult a healthcare provider before taking any herbal or alternative medicine. Caution may be appropriate when combining ginkgo products with thiazide diuretics, especially in the elderly. Blood pressure should be monitored regularly.', 'Synergism', 'In general, patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1387651, 'Glimepiride', 'Chlorthalidone', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317475, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
