INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106759, 'Iodide I-131', 'Benzylpenicillin', 'Moderate', 'INTERVAL: Certain drugs may affect the pharmacokinetics, pharmacodynamics, and/or diagnostic results of radioiodides.', NULL, 'Antithyroid agents such as carbimazole or propylthiouracil should generally be withheld for 1 week before administration of sodium iodide I-131 or I-123, and for a few days afterward.', NULL, 'Antithyroid agents such as carbimazole or propylthiouracil should generally be withheld for 1 week before administration of sodium iodide I-131 or I-123, and for a few days afterward. Salicylates, steroids, anticoagulants, antihistamines, antiparasitics, penicillins, sulfonamides, tolbutamide, thiopental, and nitroprusside should generally be withheld for 1 week. Amiodarone, lithium, and benzodiazepines should be withheld for at least 4 weeks.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106882/', '[1] "Product Information. Sodium Iodide I-123 (sodium iodide I-123)." GE Healthcare, Princeton, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767365206);
