INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106772, 'Benzylpenicillin (potassium)', 'Oxytetracycline', 'Moderate', 'Tetracyclines may reduce the effect of penicillins by inhibiting cellular protein synthesis which is necessary for cell wall synthesis inhibition by penicillins. Antagonism is more likely when low doses of either agent are administered. Therapeutic failure may result.', NULL, 'This combination should be avoided if possible.', NULL, 'This combination should be avoided if possible.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106895/', '[1] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[2] Olsson RA, Kirby JC, Romansky MJ "Pneumococcal meningitis in the adult." Ann Intern Med 55 (1961): 545-9[3] Jawetz E "Synergism and antagonism among antimicrobial drugs: a personal perspective." West J Med 123 (1975): 87-91[4] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.[5] Lepper MH, Dowling HF "Treatment of pneumococcic meningitis with penicillin compared with penicillin plus aureomycin." Arch Intern Med 88 (1951): 489-94[6] Gunnison JB, Coleman VR, Jawetz E "Interference of aureomycin and of terramycin with action of penicillin in vitro." Proc Soc Exp Biol Med 75 (1950): 549-52[7] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[8] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106773, 'Benzylpenicillin (potassium)', 'Pemetrexed', 'Moderate', 'Coadministration with drugs that are eliminated by active tubular secretion may delay and/or decrease the clearance of pemetrexed. The mechanism is competitive inhibition of the renal excretion of pemetrexed, which is primarily eliminated unchanged via glomerular filtration and active tubular secretion. Drugs (and/or their metabolites) that are thought to undergo active tubular secretion include acyclovir, allopurinol, aminosalicylic acid, cidofovir, cimetidine, creatine, dyphylline, famciclovir, famotidine, flecainide, ganciclovir, levetiracetam, metformin, methotrexate, midodrine, mycophenolic acid, oseltamivir, pralatrexate, probenecid, procainamide, quinidine, ranitidine, tenofovir, triamterene, trimethoprim, valacyclovir, valganciclovir, zalcitabine, zidovudine, and many of the beta-lactam and quinolone antibiotics.', NULL, 'Patients receiving pemetrexed in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, 'Patients receiving pemetrexed in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary. The potential for increased toxicity of pemetrexed such as bone marrow suppression should be considered. Renal function should be closely monitored during therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106896/', '[1] "Product Information. Alimta (pemetrexed)." Lilly, Eli and Company, Indianapolis, IN.', 1767365206);
