INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106802, 'Abarelix', 'Pentamidine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106925/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106803, 'Acetohexamide', 'Pentamidine', 'Moderate', 'Pentamidine may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of pentamidine has been associated with disturbances in blood glucose homeostasis due to direct toxic effects on beta cells of the pancreas. Hypoglycemia, which may be severe and/or prolonged, as well as hyperglycemia and insulin-dependent diabetes mellitus, the latter of which may be irreversible, have been reported.', NULL, 'Blood glucose should be monitored closely during and after pentamidine therapy in patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment.', NULL, 'Blood glucose should be monitored closely during and after pentamidine therapy in patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during pentamidine therapy, patients should initiate appropriate remedial therapy immediately and contact their physician. Dosage adjustments may be required if an interaction is suspected.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106926/', '[1] Shen M, Orwoll ES, Conte JE, Prince MJ "Pentamidine-induced pancreatic beta-cell dysfunction." Am J Med 86 (1989): 726-8[2] Coyle P, Carr AD, Depczynski BB, Chisholm DJ "Diabetes mellitus associated with pentamidine use in HIV-infected patients." Med J Aust 165 (1996): 587-8[3] "Product Information. Pentam 300 (pentamidine)." Fujisawa, Deerfield, IL.[4] Chan JC, Cockram CS, Critchley JA "Drug-induced disorders of glucose metabolism. Mechanisms and management." Drug Saf 15 (1996): 135-57[5] Ostrowski M, Walmsley S, Pluemecke G, Salit I, Rachlis A, Krajden S "Pentamidine-induced diabetes mellitus (PIDM)." Int Conf AIDS 9 (1993): 465[6] Liegl U, Bogner JR, Goebel FD "Insulin-dependent diabetes mellitus following pentamidine therapy in a patient with AIDS." Clin Investig 72 (1994): 1027-9[7] Bouchard PH, Sai P, Reach G, et al "Diabetes mellitus following pentamidine-induced hypoglycemia in humans." Diabetes 31 (1982): 40-5[8] "Product Information. NebuPent (pentamidine)." Fujisawa, Deerfield, IL.[9] "Product Information. Lantus (insulin glargine)" Aventis Pharmaceuticals, Swiftwater, PA.[10] Kallas EG, Galvao LL, Roland RK, Medeiros EA, Levi GC, Mendonca JS "Pentamidine induced ketoacidosis in acquired immunodeficiency syndrome patients." Int Conf AIDS 9 (1993): 474[11] Wood G, Wetzig N, Hogan P, Whitby M "Survival from pentamidine induced pancreatitis and diabetes mellitus." Aust N Z J Med 21 (1991): 341-2[12] "Product Information. Novolog (insulin aspart)" Novo Nordisk Pharmaceuticals Inc, Princeton, NJ.[13] Assan R, Mayaud C, Perronne C, Matheron S, Assan D, Zucman D, Chotard L "Pentamidine-induced derangements of glucose homeostasis: determinant roles of renal failure and drug accumulation - a study of 128 patients." Diabetes Care 18 (1995): 47-55[14] Millard PS, van der Horst C "Reversible diabetes mellitus after intravenous pentamidine." Am J Med 91 (1991): 442[15] Stahl-Bayliss CM, Kalman CM, Laskin OL "Pentamidine-induced hypoglycemia in patients with the acquired immune deficiency syndrome." Clin Pharmacol Ther 39 (1986): 271-5[16] Hardy H, Esch LD, Morse GD "Glucose disorders associated with HIV and its drug therapy." Ann Pharmacother 35 (2001): 343-51[17] Skillman TG, Feldman JM "The pharmacol