INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106819, 'Eribulin', 'Pentamidine', 'Moderate', 'Eribulin may cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if eribulin is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if eribulin is used in combination with other drugs that can prolong the QT interval. Baseline and periodic monitoring of electrocardiograms and serum electrolytes (potassium, magnesium) should be considered, and hypokalemia or hypomagnesemia corrected prior to initiating treatment with eribulin. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106942/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Halaven (eribulin)." Eisai Inc, Teaneck, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106820, 'Ertugliflozin', 'Pentamidine', 'Moderate', 'Pentamidine may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of pentamidine has been associated with disturbances in blood glucose homeostasis due to direct toxic effects on beta cells of the pancreas. Hypoglycemia, which may be severe and/or prolonged, as well as hyperglycemia and insulin-dependent diabetes mellitus, the latter of which may be irreversible, have been reported.', NULL, 'Blood glucose should be monitored closely during and after pentamidine therapy in patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment.', NULL, 'Blood glucose should be monitored closely during and after pentamidine therapy in patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during pentamidine therapy, patients should initiate appropriate remedial therapy immediately and contact their physician. Dosage adjustments may be required if an interaction is suspected.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106943/', '[1] Shen M, Orwoll ES, Conte JE, Prince MJ "Pentamidine-induced pancreatic beta-cell dysfunction." Am J Med 86 (1989): 726-8[2] Coyle P, Carr AD, Depczynski BB, Chisholm DJ "Diabetes mellitus associated with pentamidine use in HIV-infected patients." Med J Aust 165 (1996): 587-8[3] "Product Information. Pentam 300 (pentamidine)." Fujisawa, Deerfield, IL.[4] Chan JC, Cockram CS, Critchley JA "Drug-induced disorders of glucose metabolism. Mechanisms and management." Drug Saf 15 (1996): 135-57[5] Ostrowski M, Walmsley S, Pluemecke G, Salit I, Rachlis A, Krajden S "Pentamidine-induced diabetes mellitus (PIDM)." Int Conf AIDS 9 (1993): 465[6] Liegl U, Bogner JR, Goebel FD "Insulin-dependent diabetes mellitus following pentamidine therapy in a patient with AIDS." Clin Investig 72 (1994): 1027-9[7] Bouchard PH, Sai P, Reach G, et al "Diabetes mellitus following pentamidine-induced hypoglycemia in humans." Diabetes 31 (1982): 40-5[8] "Product Information. NebuPent (pentamidine)." Fujisawa, Deerfield, IL.[9] "Product Information. Lantus (insulin glargine)" Aventis Pharmaceuticals, Swiftwater, PA.[10] Kallas EG, Galvao LL, Roland RK, Medeiros EA, Levi GC, Mendonca JS "Pentamidine induced ketoacidosis in acquired immunodeficiency syndrome patients." Int Conf AIDS 9 (1993): 474[11] Wood G, Wetzig N, Hogan P, Whitby M "Survival from pentamidine induced pancreatitis and diabetes mellitus." Aust N Z J Med 21 (1991): 341-2[12] "Product Information. Novolog (insulin aspart)" Novo Nordisk Pharmaceuticals Inc, Princeton, NJ.[13] Assan R, Mayaud C, Perronne C, Matheron S, Assan D, Zucman D, Chotard L "Pentamidine-induced derangements of glucose homeostasis: determinant roles of renal failure and drug accumulation - a study of 128 patients." Diabetes Care 18 (1995): 47-55[14] Millard PS, van der Horst C "Reversible diabetes mellitus after intravenous pentamidine." Am J Med 91 (1991): 442[15] Stahl-Bayliss CM, Kalman CM, Laskin OL "Pentamidine-induced hypoglycemia in patients with the acquired immune deficiency syndrome." Clin Pharmacol Ther 39 (1986): 271-5[16] Hardy H, Esch LD, Morse GD "Glucose disorders associated with HIV and its drug therapy." Ann Pharmacother 35 (2001): 343-51[17] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[18] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[19] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[20] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of diabetes related complications. American Diabetes Association." Diabetes Care 25(Suppl 1) (2002): S50-S60[21] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[22] Jerntorp P, Almer LO, Holin H, et al "Plasma chlorpropamide: a critical factor in chlorpropamide-alcohol flush." Eur J Clin Pharmacol 24 (1983): 237-42[23] Hartling SG, Faber OK, Wegmann ML, Wahlin-Boll E, Melander A "Interaction of ethanol and glipizide in humans." Diabetes Care 10 (1987): 683-6[24] Barnett AH, Spiliopoulos AJ, Pyke DA, et al "Metabolic studies in chlorpropamide-alcohol flush positive and negative type 2 (non-insulin dependent) diabetic patients with and without retinopathy." Diabetologia 24 (1983): 213-5[25] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[26] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106821, 'Escitalopram', 'Pentamidine', 'Major', 'Escitalopram can cause dose-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if escitalopram is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if escitalopram is used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. When escitalopram is used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106944/', '[1] Castro VM, Clements CC, Murphy SN, et al. "QT interval and antidepressant use: a cross sectional study of electronic health records." BMJ 346 (2013): f288[2] "Product Information. Lexapro (escitalopram)." Forest Pharmaceuticals, St. Louis, MO.[3] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[9] Health Canada "Antidepressant Cipralex (escitalopram): Updated information regarding dose-related heart risk. Available from: URL: http://www.hc-sc.gc.ca/ahc-asc/media/advisories-avis/_2012/2012_63-eng.php." ([2012 May 7]):[10] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[12] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[13] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106822, 'Esomeprazole', 'Pentamidine', 'Moderate', 'Chronic use of proton pump inhibitors (PPIs) may induce hypomagnesemia, and the risk may be increased during concomitant use of diuretics or other agents that can cause magnesium loss. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms. Hypomagnesemia can also cause impaired parathyroid hormone secretion, which may lead to hypocalcemia. In approximately 25% of the cases of PPI-associated hypomagnesemia reviewed by the FDA, the condition did not resolve with magnesium supplementation alone but also required discontinuation of the PPI.', NULL, 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, and tacrolimus.', NULL, 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, and tacrolimus. Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106945/', '[1] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Low magnesium levels can be associated with long-term use of proton pump inhibitor drugs (PPIs). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm245011.htm." ([2011 Mar 2]):[2] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106823, 'Etelcalcetide', 'Pentamidine', 'Major', 'Concomitant use of etelcalcetide with other medicines known to lower serum calcium may increase the risk of hypocalcemia. The proposed mechanism is additive reduction in serum calcium levels. Significant decreases in serum calcium may cause paraesthesias, myalgias, muscle spasms, seizures, QT interval prolongation, and ventricular arrhythmia. Etelcalcetide given alone may lead to severe hypocalcemia requiring concomitant treatment with therapies to increase serum calcium levels.', NULL, 'Close monitoring of corrected serum calcium levels is recommended if etelcalcetide is used in combination with other medications known to lower serum calcium, such as denosumab, calcitonin, foscarnet, bisphosphonates, or loop diuretics.', NULL, 'Close monitoring of corrected serum calcium levels is recommended if etelcalcetide is used in combination with other medications known to lower serum calcium, such as denosumab, calcitonin, foscarnet, bisphosphonates, or loop diuretics. It is recommended that the patient''s corrected serum calcium level should be measured within one week of initiation or dose adjustment of etelcalcetide and monitored approximately every 4 weeks. In addition, the manufacturer product labeling should be consulted for management guidelines in the event of a reduction in corrected serum calcium levels below the lower limit of the normal range and/or symptoms of hypocalcemia. Patients should also be advised to contact their health care professional if they experience symptoms of hypocalcemia, such as numbness or tingling in the fingers, toes, or around the mouth, muscle spasms, twitches or cramps, seizures, confusion, and irregular heartbeats.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106946/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106824, 'Etidronic acid', 'Pentamidine', 'Moderate', 'Coadministration of bisphosphonates with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure.', NULL, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides, polypeptide and polymyxin antibiotics, vancomycin, amphotericin B, adefovir, cidofovir, tenofovir, foscarnet, cisplatin, gallium nitrate, lithium, mesalamine, certain immunosuppressants, intravenous pentamidine, high intravenous dosages of methotrexate, high dosages of nonsteroidal anti-inflammatory agents). Renal function and serum electrolytes should be monitored. Patients should have serum creatinine assessed prior to each treatment, and treatment should be withheld for renal deterioration.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106947/', '[1] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Kanis JA, Preston CJ, Yates AJ, Percival RC, Mundy KI, Russell RG "Effects of intravenous diphosphonates on renal function." Lancet 1 (1983): 1328[3] Chang JT, Green L, Beitz J "Renal failure with the use of zoledronic acid." N Engl J Med 349 (2003): 1676-9; discussion 1676-9[4] "Product Information. Aredia (pamidronate)." Novartis Pharmaceuticals, East Hanover, NJ.[5] Lockridge L, Papac RJ, Perazella MA "Pamidronate-associated nephrotoxicity in a patient with Langerhans''s histiocytosis." Am J Kidney Dis 40 (2002): E2[6] Osullivan TL, Akbari A, Cadnapaphornchai P "Acute renal failure associated with the administration of parenteral etidronate." Ren Fail 16 (1994): 767-73[7] Zazgornik J, Grafinger P, Biesenbach G, Hubmann R, Fridrik M "Acute renal failure and alendronate." Nephrol Dial Transplant 12 (1997): 2797-8[8] Banerjee D, Asif A, Striker L, Preston RA, Bourgoignie JJ, Roth D "Short-term, high-dose pamidronate-induced acute tubular necrosis: The postulated mechanisms of bisphosphonate nephrotoxicity." Am J Kidney Dis 41 (2003): E18[9] Markowitz GS, Fine PL, Stack JI, et al. "Toxic acute tubular necrosis following treatment with zoledronate (Zometa)." Kidney Int 64 (2003): 281-289[10] "Product Information. Boniva (ibandronate)." Roche Laboratories, Nutley, NJ.[11] "Product Information. Ostac (clodronate)." Hoffmann-La Roche Limited, Mississauga, IA.[12] Janssen Van Doorn K, Neyns B, Van Der Niepen P, Verbeelen D "Pamidronate-related nephrotoxicity (tubulointerstitial nephritis) in a patient with osteolytic bone metastases." Nephron 89 (2001): 467-8[13] "Product Information. Didronel Tablets (etidronate)." Procter and Gamble Pharmaceuticals, Cincinnati, OH.[14] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106825, 'Everolimus', 'Pentamidine', 'Major', 'Coadministration of macrolide immunosuppressants with other nephrotoxic agents may increase the risk and/or severity of renal impairment due to additive adverse effects on the kidney. Clinical experience in coadministration with cyclosporine indicates increased renal toxicity as evidenced by increased serum creatinine and decreased glomerular filtration rate. An interaction with ibuprofen resulting in acute renal failure was suspected in two liver transplant patients who had been stabilized on tacrolimus.', NULL, 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.', NULL, 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; cyclosporine; deferasirox; gallium nitrate; lithium; mesalamine; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be closely monitored both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106948/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Sheiner PA, Mor E, Chodoff L, Glabman S, Emre S, Schwartz ME, Miller CM "Acute renal, failure associated with the use of ibuprofen in two liver transplant recipients on FK506." Transplantation 57 (1994): 1132-3[4] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (106826, 'Exenatide', 'Pentamidine', 'Moderate', 'Drugs that affect renal function and/or hydration status may potentiate the adverse renal effects of exenatide. The mechanism of interaction is unknown. Exenatide has not been found to be directly nephrotoxic in preclinical or clinical studies.', NULL, 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', NULL, 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106949/', '[1] "Product Information. Byetta (exenatide)." Amylin Pharmaceuticals Inc, San Diego, CA.[2] "Product Information. Byetta (exenatide)." Amylin Pharmaceuticals Inc, San Diego, CA.', 1767365206);
