INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (107403, 'Trimethoprim', 'Perindopril', 'Major', 'The use of trimethoprim in combination with other potassium-sparing drugs or potassium salts may increase the risk of hyperkalemia. Trimethoprim inhibits sodium reabsorption and potassium excretion by blocking sodium channels in the renal distal tubules.', NULL, 'Serum potassium and sodium levels as well as renal function should be closely monitored during coadministration of trimethoprim with other potassium-sparing drugs or potassium salts, particularly in patients receiving high-dose or long-term trimethoprim treatment and in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration.', NULL, 'Serum potassium and sodium levels as well as renal function should be closely monitored during coadministration of trimethoprim with other potassium-sparing drugs or potassium salts, particularly in patients receiving high-dose or long-term trimethoprim treatment and in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. A dosage reduction of trimethoprim is recommended in renal dysfunction (50% reduction for CrCl between 15 and 30 mL/min). Patients should be given dietary counseling to avoid excessive intake of potassium-rich foods and salt substitutes, and advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat. Trimethoprim should be discontinued if hyperkalemia occurs.', 'Excretion', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'http