INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (107418, 'Doxorubicin (liposomal)', 'Trastuzumab', 'Major', 'The use of trastuzumab and anthracyclines in combination has been associated with a high risk of cardiotoxicity. Trastuzumab and agents in the anthracycline class are individually cardiotoxic and may have additive effects during coadministration.', NULL, 'Trastuzumab and cardiotoxic agents such as anthracyclines should not be used in combination except in settings where cardiac monitoring is possible.', NULL, 'Trastuzumab and cardiotoxic agents such as anthracyclines should not be used in combination except in settings where cardiac monitoring is possible. The use of anthracycline-based therapy should be avoided, if possible, for up to 7 months after stopping trastuzumab. If anthracyclines must be used sooner, the patient''s cardiac function should be closely monitored.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107542/', '[1] Strasser F, Betticher DC, Suter TM "Trastuzumab and breast cancer." N Engl J Med 345 (2001): 996[2] "Product Information. DOXOrubicin Hydrochloride (DOXOrubicin)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Herceptin (trastuzumab)." Genentech, South San Francisco, CA.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (107419, 'Efalizumab', 'Trastuzumab', 'Moderate', 'The use of efalizumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Efalizumab alone can cause serious infections requiring hospitalization, and the risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated.', NULL, 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated. Patients receiving other immunosuppressive agents should not be treated with efalizumab because of the possibility of excessive immunosuppression. The duration of the period following treatment with efalizumab before one should consider starting other immunosuppressive therapy has not been evaluated.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107543/', '[1] "Product Information. Raptiva (efalizumab)." Genentech, South San Francisco, CA.', 1767365206);
