INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (107749, 'Verteporfin', 'Phentolamine', 'Moderate', 'Drugs that decrease clotting, vasoconstriction, or platelet aggregation may interfere with the therapeutic effect of verteporfin. These agents may antagonize the physiologic responses to local damage of neovascular endothelium induced by activated verteporfin, which include the release of procoagulant and vasoactive factors that result in vessel occlusion. However, the clinical significance is unknown.', NULL, 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy.', NULL, 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy. Otherwise, the potential for diminished efficacy of verteporfin should be considered.', 'Antagonism', 'Aflibercept, Bevacizumab', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107873/', '[1] "Product Information. Visudyne (verteporfin)" Valeant Pharmaceuticals, Costa Mesa, CA.[2] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (107750, 'Zaleplon', 'Phentolamine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Cobicistat, Deferoxamine, Levoleucovorin, Arginine, More', 'Suvorexant, Triazolam, Methohexital, Methylphenobarbital, Pentobarbital, Dichloralphenazone, Valerian, Quazepam, Ramelteon, Lemborexant, Ethchlorvynol, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107874/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-as