INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (107916, 'Glimepiride', 'Fosaprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 2C9. The mechanism is accelerated clearance due to induction of CYP450 2C9 activity by aprepitant.', NULL, 'Caution is advised if aprepitant or fosaprepitant must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if aprepitant or fosaprepitant must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', 'Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Miglitol, Albiglutide, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/108040/', '[1] "Product Information. Emend (aprepitant)." Merck & Company Inc, West Point, PA.', 1767369485);
