INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (108177, 'Salicylic acid (sodium)', 'Potassium citrate', 'Moderate', 'Agents that cause urinary alkalinization can reduce serum salicylate concentrations in patients receiving anti-inflammatory dosages of aspirin or other salicylates. The mechanism involves reduction in salicylate renal tubular reabsorption due to increased urinary pH, resulting in increased renal salicylate clearance especially above urine pH of 7. This interaction is sometimes exploited in the treatment of salicylate toxicity.', NULL, 'Patients treated chronically with urinary alkalinizers and large doses of salicylates (i.', NULL, 'Patients treated chronically with urinary alkalinizers and large doses of salicylates (i.e. 3 g/day or more) should be monitored for potentially diminished or inadequate analgesic and anti-inflammatory effects, and the salicylate dosage adjusted if necessary.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/108301/', '[1] Prescott LF, Balali-Mood M, Critchley JA, Johnstone AF, Proudfoot AT "Diuresis or urinary alkalinisation for salicylate poisoning?" Br Med J (Clin Res Ed) 285 (1982): 1383-6[2] Brouwers JRBJ, Desmet PAGM "Pharmacokinetic-pharmacodynamic drug interactions with nonsteroidal anti-inflammatory drugs." Clin Pharmacokinet 27 (1994): 462-85[3] Berg KJ "Acute effects of acetylsalicylic acid in patients with chronic renal insufficiency." Eur J Clin Pharmacol 11 (1977): 111-6[4] Balali-Mood M, Prescott LF "Failure of alkaline diuresis to enhance diflunisal elimination." Br J Clin Pharmacol 10 (1980): 163-5[5] Berg KJ "Acute acetylsalicylic acid poisoning: treatment with forced alkaline diuresis and diuretics." Eur J Clin Pharmacol 12 (1977): 111-6', 1767363023);
