INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (108180, 'Eplerenone', 'Monopotassium phosphate', 'Major', 'The principal risk of eplerenone is hyperkalemia. Coadministration with potassium salts or potassium-sparing diuretics may increase this risk due to additive pharmacodynamic effects.', NULL, 'Given the potential for serious, sometimes fatal cardiac arrhythmias associated with hyperkalemia, the use of eplerenone with potassium salts or potassium-sparing diuretics is considered contraindicated.', NULL, 'Given the potential for serious, sometimes fatal cardiac arrhythmias associated with hyperkalemia, the use of eplerenone with potassium salts or potassium-sparing diuretics is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/108304/', '[1] Marcy TR, Ripley TL "Aldosterone antagonists in the treatment of heart failure." Am J Health Syst Pharm 63 (2006): 49-58[2] "Product Information. Inspra (eplerenone)." Searle, Chicago, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (108181, 'Eprosartan', 'Monopotassium phosphate', 'Major', 'Concomitant use of angiotensin II receptor blockers (ARBs) and potassium salts may increase the risk of hyperkalemia. Inhibition of angiotensin II results in decreased aldosterone secretion, which in turn causes potassium retention. Risk factors for developing severe or life-threatening hyperkalemia may include renal impairment, diabetes, old age, severe or worsening heart failure, dehydration, and concomitant use of other agents that block the renin-angiotensin-aldosterone system or otherwise increase serum potassium levels.', NULL, 'Caution is advised if angiotensin II receptor blockers must be used concurrently with potassium salts, particularly in patients with renal impairment, diabetes, old age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium such as nonsteroidal anti-inflammatory drugs, beta-blockers, cyclosporine, heparin, tacrolimus, and trimethoprim.', NULL, 'Caution is advised if angiotensin II receptor blockers must be used concurrently with potassium salts, particularly in patients with renal impairment, diabetes, old age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium such as nonsteroidal anti-inflammatory drugs, beta-blockers, cyclosporine, heparin, tacrolimus, and trimethoprim. The combination should generally be avoided in these patients unless absolutely necessary and the benefits outweigh the potential risks. Serum potassium and renal function should be checked prior to initiating therapy and regularly thereafter. Patients should be given counseling on the appropriate levels of potassium and fluid intake, and advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/108305/', '[1] Walmsley RN, White GH, Cain M, McCarthy PJ, Booth J "Hyperkalemia in the elderly." Clin Chem 30 (1984): 1409-12[2] Jarman PR, Mather HM "Diabetes may be independent risk factor for hyperkalaemia." BMJ 327 (2003): 812[3] Obialo CI, Ofili EO, Mirza T "Hyperkalemia in congestive heart failure patients aged 63 to 85 years with subclinical renal disease." Am J Cardiol 90 (2002): 663-5[4] Jarman PR, Kehely AM, Mather HM "Hyperkalaemia in diabetes: prevalence and associations." Postgrad Med J 71 (1995): 551-2[5] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.[6] Large DM, Carr PH, Laing I, Davies M "Hyperkalaemia in diabetes mellitus--potential hazards of coexisting hyporeninaemic hypoaldosteronism." Postgrad Med J 60 (1984): 370-3[7] Perazella MA "Drug-induced hyperkalemia: old culprits and new offenders." Am J Med 109 (2000): 307-14[8] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[9] Perazella MA, Mahnensmith RL "Hyperkalemia in the elderly: drugs exacerbate impaired potassium homeostasis." J Gen Intern Med 12 (1997): 646-56[10] Lawson DH "Adverse reactions to potassium chloride." Q J Med 43 (1974): 433-40[11] Lawson DH, O''Connor PC, Jick H "Drug attributed alterations in potassium handling in congestive cardiac failure." Eur J Clin Pharmacol 23 (1982): 21-5[12] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[13] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.', 1767363023);
