INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (108216, 'Triamterene', 'Monopotassium phosphate', 'Major', 'The concomitant use of potassium-sparing diuretics and potassium preparations may result in hyperkalemia.', NULL, 'The use of potassium preparations in patients treated with potassium-sparing diuretics is generally not recommended.', NULL, 'The use of potassium preparations in patients treated with potassium-sparing diuretics is generally not recommended. Caution is advised if coadministration is required, particularly in patients with renal impairment, diabetes, old age, worsening heart failure, and/or a risk for dehydration. Serum potassium and renal function should be checked regularly, and oral potassium supplementation should be avoided in patients with severe congestive heart failure who have serum potassium levels above 3.5 mEq/L. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/108340/', '[1] Shapiro S, Slone D, Lewis GP, Jick H "Fatal drug reactions among medical inpatients." JAMA 216 (1971): 467-72[2] McNay JL, Oran E "Possible predisposition of diabetic patients to hyperkalemia following administration of potassium-retaining diuretic, amiloride (MK 870)." Metabolism 19 (1970): 58-70[3] Walmsley RN, White GH, Cain M, McCarthy PJ, Booth J "Hyperkalemia in the elderly." Clin Chem 30 (1984): 1409-12[4] Jarman PR, Kehely AM, Mather HM "Hyperkalaemia in diabetes: prevalence and associations." Postgrad Med J 71 (1995): 551-2[5