        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1388502, 'Hydralazine', 'Choline salicylate', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effect of hydralazine and dihydralazine. This interaction suggests that prostaglandins play a pivotal role in the vasodilatory effects of hydralazine.', NULL, 'Recommended management consists of monitoring the antihypertensive effectiveness of hydralazine or dihydralazine during therapy, increasing the dosage as necessary, or decreasing or substituting another drug for the NSAID.', NULL, 'Recommended management consists of monitoring the antihypertensive effectiveness of hydralazine or dihydralazine during therapy, increasing the dosage as necessary, or decreasing or substituting another drug for the NSAID.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
