INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (108767, 'Imipramine', 'Dextropropoxyphene', 'Major', 'Concomitant use of propoxyphene and tricyclic and tetracyclic antidepressants (TCAs) may result in additive central nervous system (CNS) depressant effects. Misuse of propoxyphene, either alone or in combination with other CNS depressants, has been a major cause of drug-related deaths, particularly in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', NULL, 'Caution is advised if propoxyphene is prescribed with TCAs, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', NULL, 'Caution is advised if propoxyphene is prescribed with TCAs, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. Serum TCA levels should be monitored more closely following the addition or withdrawal of propoxyphene, and the TCA dosage adjusted as necessary.', 'Synergism', 'Viloxazine', 'Naloxone, Naltrexone', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/108891/', '[1] Tofanetti O, Albiero L, Galatulas I, Genovese E "Enhancement of propoxyphene-induced analgesia by doxepin." Psychopharmacology (Berl) 51 (1977): 213-5[2] Jerling M, Bertilsson L, Sjoqvist F "The use of therapeutic drug monitoring data to document kinetic drug interactions: an example with amitriptyline and nortriptyline." Ther Drug Monit 16 (1994): 1-2[3] Abernethy DR, Greenblatt DJ, Steel K, Shader RI "Impairment of hepatic drug oxidation by propoxyphene." Ann Intern Med 97 (1982): 223-4[4] Shorr RI, Griffin MR, Daugherty JR, Ray WA "Opioid analgesics and the risk of hip fracture in the elderly: codeine and propoxyphene." J Gerontol 47 (1992): m111-5[5] "Product Information. Darvon (propoxyphene)." Lilly, Eli and Company, Indianapolis, IN.[6] "Product Information. Darvon (propoxyphene)." Lilly, Eli and Company, Indianapolis, IN.', 1767369485);
