INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (108894, 'Dextropropoxyphene', 'Ozanimod', 'Major', 'Due to the risk of bradycardia and atrioventricular (AV) block, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of ozanimod treatment in patients receiving drugs that prolong the QT interval.', NULL, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with ozanimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties.', 'Synergism', 'Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109018/', '[1] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[2] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (108895, 'Dextropropoxyphene', 'Paliperidone', 'Major', 'Sedatives, tranquilizers, muscle relaxants, antidepressants, and other central nervous system (CNS) depressants may have additive CNS- and/or respiratory-depressant effects with propoxyphene. Misuse of propoxyphene, either alone or in combination with other CNS depressants, has been a major cause of drug-related deaths, particularly in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. Pharmacokinetically, propoxyphene is an inhibitor of CYP450 2D6 and may increase the plasma concentrations of many psychotropic agents that are metabolized by the isoenzyme such as phenothiazines, haloperidol, risperidone, phenobarbital, and some tricyclic antidepressants and serotonin reuptake inhibitors.', NULL, 'Caution is advised if propoxyphene is used with sedatives, tranquilizers, muscle relaxants, antidepressants, and other CNS depressants, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', NULL, 'Caution is advised if propoxyphene is used with sedatives, tranquilizers, muscle relaxants, antidepressants, and other CNS depressants, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. Dosage reductions may be appropriate. Patients should be monitored for potentially excessive or prolonged CNS and respiratory depression and other CNS adverse effects. Patients should be warned not to exceed recommended dosages, to avoid alcohol, and to avoid activities requiring mental alertness until they know how these agents affect them.', 'Metabolism, Synergism', 'Acetylsalicylic acid, Naloxone, Naltrexone, Celecoxib', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109019/', '[1] Hansen BS, Dam M, Brandt J, et al "Influence of dextropropoxyphene on steady state serum levels and protein binding of three anti-epileptic drugs in man." Acta Neurol Scand 61 (1980): 357-67[2] Peterson GR, Covault HP, Hostetler RM "Acute inhibition of microsomal drug metabolism by propoxphene in narcotic tolerant/dependent mice." Life Sci 22 (1978): 2087-96[3] Abernethy DR, Greenblatt DJ, Morse DS, Shader RI "Interaction of propoxyphene with diazepam, alprazolam and lorazepam." Br J Clin Pharmacol 19 (1985): 51-7[4] Abernethy DR, Greenblatt DJ, Steel K, Shader RI "Impairment of hepatic drug oxidation by propoxyphene." Ann Intern Med 97 (1982): 223-4[5] Puckett WH Jr, Visconti JA "Orphenadrine and propoxyphene." N Engl J Med 283 (1970): 544[6] Pearson RE, Salter FJ "Drug interaction? Orphenadrine with propoxyphene." N Engl J Med 282 (1970): 1215[7] Shorr RI, Griffin MR, Daugherty JR, Ray WA "Opioid analgesics and the risk of hip fracture in the elderly: codeine and propoxyphene." J Gerontol 47 (1992): m111-5[8] "Product Information. Darvon (propoxyphene)." Lilly, Eli and Company, Indianapolis, IN.[9] "Product Information. Darvon (propoxyphene)." Lilly, Eli and Company, Indianapolis, IN.[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[12] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[13] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
