INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (108908, 'Dextropropoxyphene', 'Perphenazine', 'Major', 'Concomitant use of propoxyphene and phenothiazines may result in additive central nervous system (CNS) depressant effects. Misuse of propoxyphene, either alone or in combination with other CNS depressants, has been a major cause of drug-related deaths, particularly in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', NULL, 'Caution is advised if propoxyphene is prescribed with phenothiazines, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', NULL, 'Caution is advised if propoxyphene is prescribed with phenothiazines, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. Dosage reductions may be required. Patients should be warned not to exceed recommended dosages, to avoid alcohol, and to notify their physician if they experience symptoms of toxicity such as lethargy, excessive sedation, dizziness, syncope, seizures, and/or irregular heartbeat. In addition, they should avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Naloxone, Naltrexone', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109032/', '[1] Shorr RI, Griffin MR, Daugherty JR, Ray WA "Opioid analgesics and the risk of hip fracture in the elderly: codeine and propoxyphene." J Gerontol 47 (1992): m111-5[2] Abernethy DR, Greenblatt DJ, Steel K, Shader RI "Impairment of hepatic drug oxidation by propoxyphene." Ann Intern Med 97 (1982):