INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109299, 'Dalfopristin', 'Bexarotene', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may theoretically increase the plasma concentrations of bexarotene. In vitro studies suggest that bexarotene is metabolized by CYP450 3A4. However, concomitant administration with multiple doses of ketoconazole, a potent CYP450 3A4 inhibitor, did not alter bexarotene plasma concentrations, which would imply that bexarotene elimination is not substantially dependent on CYP450 3A4 metabolism in vivo. Conversely, bexarotene appears to be an inducer of CYP450 3A4 and may reduce the plasma concentrations of CYP450 3A4 inhibitors that are also substrates of the isoenzyme, such as azole antifungals, macrolides, some calcium channel blockers, mifepristone, and nefazodone.', NULL, '-', NULL, '-', 'Others', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, More', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109423/', '[1] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[2] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109300, 'Dalfopristin', 'Bosentan', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 and/or 3A4 may increase the plasma concentrations of bosentan, which is metabolized by these isoenzymes. Concomitant administration of both a CYP450 2C9 inhibitor and a CYP450 3A4 inhibitor may lead to even larger increases in plasma concentrations of bosentan.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should also be considered during coadministration with CYP450 2C9 or 3A4 inhibitors.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should also be considered during coadministration with CYP450 2C9 or 3A4 inhibitors. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. Concomitant administration of bosentan with both a potent CYP450 2C9 inhibitor (e.g., fluconazole, amiodarone) and a potent CYP450 3A4 inhibitor (e.g., ketoconazole, itraconazole, ritonavir) is not recommended. Concomitant administration with dual inhibitors of CYP450 2C9 and 3A4 (e.g., delavirdine, imatinib, miconazole, voriconazole) should probably be avoided also, if possible.', 'Metabolism', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109424/', '[1] "Product Information. Tracleer (bosentan)." Acetelion Pharmaceuticals US, Inc, South San Francisco, CA.', 1767369485);
