INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109317, 'Dalfopristin', 'Cevimeline', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of cevimeline, which is metabolized by these isoenzymes.', NULL, 'Dosage adjustments and clinical monitoring may be appropriate whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy.', NULL, 'Dosage adjustments and clinical monitoring may be appropriate whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience excessive adverse cholinergic effects of cevimeline such as nausea, vomiting, diarrhea, sweating, salivation, urinary frequency, visual disturbance, confusion, tremor, palpitations, or irregular heartbeat.', 'Metabolism', 'Bethanechol, Neostigmine, Pyridostigmine, Ambenonium, Pilocarpine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109441/', '[1] "Product Information. Evoxac (cevimeline)" Daiichi Pharmaceuticals, Fort lee, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109318, 'Dalfopristin', 'Chlordiazepoxide', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', 'Meprobamate, Lorazepam, Buspirone, Hydroxyzine, Oxazepam', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109442/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)" Rhone-Poulenc Rorer, Collegeville, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109319, 'Dalfopristin', 'Chloroquine', 'Moderate', 'Coadministration with moderate or strong inhibitors of CYP450 2C8 or CYP450 3A4 may increase the plasma concentration of hydroxychloroquine and the risk of toxicities such as QT interval prolongation and ventricular arrhythmias.', NULL, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.', NULL, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor. Clinical and laboratory monitoring should be considered whenever a moderate or strong CYP450 3A4 or 2C8 inhibitor is added to or withdrawn from therapy with hydroxychloroquine or chloroquine, and the dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Artesunate, Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Primaquine, Proguanil, Sulfadoxine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109443/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109320, 'Dalfopristin', 'Cilostazol', 'Major', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C19 may increase the plasma concentrations of cilostazol and or its pharmacologically active metabolites, which are substrates of these isoenzymes. The possibility of prolonged and/or increased pharmacologic effects of cilostazol should be considered.', NULL, 'A 50% dosage reduction of cilostazol (i.', NULL, 'A 50% dosage reduction of cilostazol (i.e., 50 mg twice a day) should be considered when used with potent or moderate CYP450 3A4 and/or 2C19 inhibitors. Close clinical and laboratory monitoring is advised whenever the inhibitor is added to or withdrawn from therapy, and the cilostazol dosage adjusted as necessary. Patients should be advised to contact their physician if they experience adverse effects of cilostazol such as dizziness, nausea, diarrhea, bleeding, or irregular heartbeat.', 'Metabolism', 'Betrixaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Warfarin, Caplacizumab, Argatroban, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109444/', '[1] Suri A, Bramer SL "Effect of omeprazole on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999): 53-9[2] Suri A, Forbes WP, Bramer SL "Effects of CYP3A inhibition on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999): 61-8[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Pletal (cilostazol)." Otsuka American Pharmaceuticals, Rockville, MD.[5] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Availa