INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109358, 'Efavirenz', 'Dalfopristin', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109482/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)" Rhone-Poulenc Rorer, Collegeville, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109359, 'Elagolix', 'Dalfopristin', 'Moderate', 'Coadministration with inhibitors of the OATP 1B1 hepatic uptake transporter, CYP450 3A4 metabolic isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of elagolix. The effect of P-gp inhibitors on the pharmacokinetics of elagolix has not been studied, but some increases in elagolix exposure may occur, as it is a P-gp substrate. Increased exposure to elagolix may increase the risk of serious adverse effects such as bone loss, suicidal ideation and behavior, exacerbation of mood disorders, and hepatic transaminase elevations.', NULL, 'Caution is advised when elagolix is used with OATP 1B1, CYP450 3A4, and/or P-gp inhibitors.', NULL, 'Caution is advised when elagolix is used with OATP 1B1, CYP450 3A4, and/or P-gp inhibitors. Patients should be monitored for potentially altered effects of elagolix following the initiation or discontinuation of these inhibitors, and the elagolix dosage adjusted as necessary.', 'Metabolism', 'Nafarelin, Octreotide, Cetrorelix, Pasireotide, Ganirelix, Relugolix, Lanreotide, Gonadorelin', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109483/', '[1] "Product Information. Orilissa (elagolix)." AbbVie US LLC, North Chicago, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109360, 'Eletriptan', 'Dalfopristin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eletriptan, which is primarily metabolized by the isoenzyme.', NULL, 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics. Patients should have vital signs monitored regularly and advised to notify their physician if they experience signs and symptoms of vasospasm such as numbness, tingling, or cyanosis in the extremities; muscle pains; weakness; or chest pain or tightness. Alternatively, other 5-HT1 receptor agonists that are not metabolized by CYP450 3A4 may be considered, such as frovatriptan, naratriptan, rizatriptan, sumatriptan, and zolmitriptan.', 'Metabolism', 'Frovatriptan, Eptinezumab, Lasmiditan, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Erenumab, Clonidine, Almotriptan, Fremanezumab', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109484/', '[1] "Product Information. Relpax (eletriptan)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109361, 'Eliglustat', 'Dalfopristin', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', NULL, 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs).', NULL, 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs). In the absence of a concomitant CYP450 2D6 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to EMs treated with a moderate CYP450 3A4 inhibitor. However, eliglustat should not be used with a moderate CYP450 3A4 inhibitor in CYP450 2D6 IMs or poor metabolizers (PMs). Moderate CYP450 3A4 inhibitors include aprepitant, ciprofloxacin, clotrimazole, crizotinib, darunavir, diltiazem, dronedarone, fluconazole, fusidic acid, imatinib, isavuconazonium, miconazole, mifepristone, netupitant, quinupristin-dalfopristin, ranolazine, stiripentol, and verapamil. Potent and moderate CYP450 2D6 inhibitors include abiraterone, bupropion, celecoxib, cimetidine, cinacalcet, clobazam, darifenacin, diphenhydramine, duloxetine, fluoxetine, methotrimeprazine, mirabegron, paroxetine, propoxyphene, quinidine, ranolazine, sertraline, stiripentol, and terbinafine.', 'Metabolism', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109485/', '[1] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109362, 'Encorafenib', 'Dalfopristin', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of encorafenib, which is primarily metabolized by the isoenzyme. Increased exposure to encorafenib may increase the risk of serious and life-threatening adverse effects such as hemorrhage, uveitis, QT prolongation, hepatotoxicity, dermatologic reactions, and new malignancies.', NULL, 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the encorafenib dose to one-half of the dose used prior to addition of the moderate CYP450 3A4 inhibitor. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the encorafenib dose that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Dabrafenib, Dacomitinib, Vemurafenib, Vandetanib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, Erlotinib, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109486/', '[1] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc., Boulder, CO.[2] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc., Boulder, CO.[3] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc., Boulder, CO.', 1767369485);
