INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109380, 'Felodipine', 'Dalfopristin', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', 'Propranolol, Metoprolol, Carvedilol, Nebivolol, Bisoprolol, Sotalol, Acetylsalicylic acid, Atenolol, Clevidipine, Celecoxib, Enalapril, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109504/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)" Rhone-Poulenc Rorer, Collegeville, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109381, 'Fentanyl', 'Dalfopristin', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fentanyl, which is primarily metabolized by the isoenzyme. Increased fentanyl concentrations could increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Conversely, discontinuation of a CYP450 3A4 inhibitor could decrease fentanyl plasma concentrations, decrease opioid efficacy, and possibly even lead to a withdrawal syndrome in patients who had developed physical dependence to fentanyl.', NULL, 'Patients receiving fentanyl with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed.', NULL, 'Patients receiving fentanyl with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed. This is particularly important when an inhibitor is added after a stable dose of fentanyl has been achieved. Patients and/or their caregivers should be advised to seek medical attention if potential signs and symptoms of toxicity occur, such as dizziness, confusion, fainting, extreme sedation, unresponsiveness, bradycardia, slow or difficult breathing, and shortness of breath.', 'Metabolism', 'Halothane, Thiopental, Remifentanil, Sodium oxybate, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Propofol, Enflurane, Esketamine, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109505/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Labroo RB, Paine MF, Thummel KE, Kharasch ED "Fentanyl metabolism by human hepatic and intestinal cytochrome P450 3A4: implicaitons for interindividual variability in disposition, efficacy, and drug interactions." Drug Metab Dispos 25 (1997): 1072-80[3] "Product Information. Actiq (fentanyl)." Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[5] Tateishi T, Krivoruk Y, Ueng YF, Wood AJ, Guengerich FP, Wood M "Identification of human cytochrome P-450 3A4 as the enzyme responsible for fentanyl and sufentanil N-dealkylation." Anesth Analg 82 (1996): 167-72[6] Ibrahim AE, Feldman J, Karim A, Kharasch ED "Simultaneous Assessment of Drug Interactions with Low- and High-Extraction Opioids: Application to Parecoxib Effects on the Pharmacokinetics and Pharmacodynamics of Fentanyl and Alfentanil." Anesthesiology 98 (2003): 853-861[7] "Product Information. Duragesic Transdermal System (fentanyl)." Janssen Pharmaceutica, Titusville, NJ.[8] Kharasch ED, Whittington D, Hoffer C "Influence of Hepatic and Intestinal Cytochrome P4503A Activity on the Acute Disposition and Effects of Oral Transmucosal Fentanyl Citrate." Anesthesiology 101 (2004): 729-737[9] "Product Information. Duragesic Transdermal System (fentanyl)." Janssen Pharmaceutica, Titusville, NJ.[10] Labroo RB, Paine MF, Thummel KE, Kharasch ED "Fentanyl metabolism by human hepatic and intestinal cytochrome P450 3A4: implicaitons for interindividual variability in disposition, efficacy, and drug interactions." Drug Metab Dispos 25 (1997): 1072-80[11] Tateishi T, Krivoruk Y, Ueng YF, Wood AJ, Guengerich FP, Wood M "Identification of human cytochrome P-450 3A4 as the enzyme responsible for fentanyl and sufentanil N-dealkylation." Anesth Analg 82 (1996): 167-72[12] "Product Information. Actiq (fentanyl)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109382, 'Fesoterodine', 'Dalfopristin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of fesoterodine should be considered. Because 5-hydroxymethyl tolterodine is also metabolized by CYP450 2D6, the clinical significance of the interaction may be greater in patients who are CYP450 2D6-deficient, or so-called poor metabolizers of CYP450 2D6 (approximately 7% of Caucasians and less than 2% of Asians and individuals of African descent) who may rely more on the 3A4 metabolic pathway for clearance of the drug.', NULL, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary.', NULL, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary. Patients should be advised to notify their physician if they experience potential adverse effects of fesoterodine such as irregular heartbeat, blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, gastrointestinal upset, or constipation.', 'Metabolism', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109506/', '[1] "Product Information. Toviaz (fesoterodine)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Toviaz (fesoterodine)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109383, 'Flibanserin', 'Dalfopristin', 'Major', 'Coadministration with moderate and potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of flibanserin, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19. High plasma levels of flibanserin may increase the risk of severe hypotension, syncope, and central nervous system depression.', NULL, 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated. When initiating flibanserin following treatment with a moderate or potent CYP450 3A4 inhibitor, the manufacturer recommends waiting until 2 weeks after the last dose of CYP450 3A4 inhibitor. Conversely, if a moderate or potent CYP450 3A4 inhibitor is required during flibanserin therapy, flibanserin should be discontinued at least 2 days before starting the CYP450 3A4 inhibitor.', 'Metabolism', 'Ritodrine, Bremelanotide, Cabergoline, Black cohosh, Naproxen, Ibuprofen', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109507/', '[1] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.[2] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.', 1767369485);
