INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109448, 'Dalfopristin', 'Mirabegron', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of mirabegron, which has been shown in vitro to be a substrate of the isoenzyme. However, in vivo results indicate that CYP450 3A4 may play a limited role in the overall elimination. No dosage adjustment is recommended when mirabegron is administered in combination with ketoconazole and probably other CYP450 3A4 inhibitors.', NULL, '-', NULL, '-', 'Metabolism', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109572/', '[1] "Product Information. Myrbetriq (mirabegron)." Astellas Pharma US, Inc, Deerfield, IL.[2] "Product Information. Myrbetriq (mirabegron)." Astellas Pharma US, Inc, Deerfield, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109449, 'Dalfopristin', 'Mirtazapine', 'Moderate', 'Coadministration of mirtazapine with drugs that inhibit one or more of its metabolic pathways may result in increased plasma concentrations of mirtazapine. In vitro data from human liver microsomes indicate that CYP450 2D6 and 1A2 are involved in the formation of the 8-hydroxy metabolite, while CYP450 3A4 is primarily responsible for the formation of the N-desmethyl and N-oxide metabolites.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of mirtazapine should be considered during coadministration of drugs that inhibit CYP450 1A2, 2D6 and/or 3A4.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of mirtazapine should be considered during coadministration of drugs that inhibit CYP450 1A2, 2D6 and/or 3A4. Therapeutic response to mirtazapine should be monitored more closely following initiation, discontinuation, or dosing change of CYP450 inhibitors, and the mirtazapine dosage adjusted as necessary.', 'Metabolism', 'Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, Amoxapine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109573/', '[1] Sitsen JM, Maris FA, Timmer CJ "Concomitant use of mirtazapine and cimetidine: a drug-drug interaction study in healthy male subjects." Eur J Clin Pharmacol 56 (2000): 389-94[2] "Product Information. Remeron (mirtazapine)." Organon, West Orange, NJ.[3] Stormer E, von Moltke LL, Shader RI, Greenblatt DJ "Metabolism of the antidepressant mirtazapine in vitro: contribution of cytochromes P-450 1A2, 2D6, and 3A4" Drug Metab Dispos 28 (2000): 1168-75[4] Okubo M, Murayama N, Miura J, Chiba Y, Yamazaki H "Effects of cytochrome P450 2D6 and 3A5 genotypes and possible coadministered medicines on the metabolic clearance of antidepressant mirtazapine in Japanese patients." Biochem Pharmacol 93 (2015): 104-9[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109450, 'Dalfopristin', 'Modafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme. Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4. The clinical significance of this potential interaction is unknown. Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', NULL, '-', NULL, '-', 'Metabolism', 'Amphetamine, Pemoline, Atomoxetine, Metamfetamine, Lisdexamfetamine, Solriamfetol, Caffeine, Methylphenidate, Dexmethylphenidate, Dextroamphetamine, Citicoline', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109574/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.[3] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[4] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[5] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109451, 'Dalfopristin', 'Mycophenolate mofetil', 'Moderate', 'Theoretically, drugs that alter the gastrointestinal flora (e.g., antibiotics) may reduce the oral bioavailability of mycophenolic acid products. Some investigators believe that antimicrobials may interfere with the enterohepatic recirculation of mycophenolic acid by decreasing bacterial hydrolytic enzymes in the gastrointestinal tract that are responsible for regenerating mycophenolic acid from its glucuronide metabolite following first-pass metabolism.', NULL, 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', NULL, 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109575/', '[1] "Product Information. CellCept (mycophenolate mofetil)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
