INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109464, 'Dalfopristin', 'Nitisinone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of and exposure to, nitisinone. The proposed mechanism is inhibition of CYP450 3A4-mediated metabolism of nitisinone, which has been shown in in vitro studies to be metabolized by this isoenzyme.', NULL, 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inhibitors.', NULL, 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inhibitors. Pharmacologic response to nitisinone should be monitored whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the nitisinone dosage adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109588/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med J 90 (2000): 41[5] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs - how significant is the interaction." Clin Pharmacokinet 26 (1994): 91-8[6] Bailey DG, D