    ) VALUES (1389120, 'Cimetidine', 'Bromfenac', 'Minor', 'Cimetidine may increase the plasma bromfenac concentration and decrease its clearance. Clinical monitoring of patient response and tolerance is recommended.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
