        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389150, 'Desvenlafaxine', 'Cimetidine', 'Minor', 'Coadministration with cimetidine may increase the plasma concentrations of venlafaxine but not O-desmethylvenlafaxine (ODV), its pharmacologically active metabolite. The mechanism is inhibition of hepatic first-pass metabolism of venlafaxine by cimetidine. However, for patients with preexisting hypertension, elderly patients, and patients with hepatic dysfunction, the interaction has not been studied and may be more pronounced.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
