    ) VALUES (1389154, 'Dienestrol (topical)', 'Cimetidine', 'Minor', 'Theoretically, pharmacologic effects of estrogens may be enhanced during treatment with cimetidine due to increased serum concentrations of endogenous estradiol. The mechanism is cimetidine inhibition of the 2-hydroxylation of estradiol.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
