        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389159, 'Disopyramide', 'Cimetidine', 'Minor', 'small study (n=6) has reported that concurrent administration of oral disopyramide and cimetidine resulted in a 19% increase in the peak plasma concentration of disopyramide, a 9% increase in the area under the plasma concentration-time curve, and a 20% increase in the total amount of disopyramide excreted unchanged in the urine. (Cimetidine did not alter the absorption of a single 150 mg dose of disopyramide administered by an IV bolus.) The mechanism of this interaction may be an increase in the absorption of disopyramide caused by cimetidine. Patients should be monitored for evidence of an interaction.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
