    ) VALUES (1389160, 'Cimetidine', 'Valproic acid', 'Minor', 'Cimetidine may inhibit the hepatic metabolism of valproate. The clinical significance of this interaction is unknown. The interaction may be most significant in patients with serum valproic acid levels near the upper limit of the therapeutic range.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
