    ) VALUES (1389161, 'Dobutamine', 'Cimetidine', 'Moderate', 'Limited data suggest that dobutamine, when used in combination with cimetidine, may lead to increased pressor effects resulting in hypertension. The mechanism is unknown and causality has not been definitely established. It is not known if this reaction would occur with other H-2 antagonists.', NULL, 'Monitor the patient''s blood pressure more closely and adjust the dose of dobutamine if needed.', NULL, 'Monitor the patient''s blood pressure more closely and adjust the dose of dobutamine if needed.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
