        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389166, 'Doravirine', 'Cimetidine', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of doravirine, which is primarily metabolized by the isoenzyme.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
