        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389179, 'Epirubicin', 'Cimetidine', 'Major', 'Cimetidine has been reported to increase the area under the concentration time curve of epirubicin by 50%. The mechanism is unknown, but may be related to inhibition by cimetidine of hepatic enzymes responsible for the metabolism of epirubicin.', NULL, 'Cimetidine should not be given concurrently with epirubicin.', NULL, 'Cimetidine should not be given concurrently with epirubicin. Ranitidine has not been shown to affect the pharmacokinetic disposition of epirubicin and may provide an alternative to cimetidine.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
