        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389180, 'Ergocalciferol', 'Cimetidine', 'Minor', 'Cimetidine may interfere with the metabolism of vitamin D in the liver to its biologically active form. The proposed mechanism is inhibition of hepatic vitamin D 25-hydroxylase by cimetidine. However, since the formation of active vitamin D metabolites in the kidneys is not affected, the interaction is likely to be of limited clinical importance in the general population.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
