        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389184, 'Erythromycin', 'Cimetidine', 'Moderate', 'Concomitant use of cimetidine and erythromycin may result in higher-than-normal plasma levels of erythromycin through inhibition of CYP450 metabolism by cimetidine.', NULL, 'The patient should be monitored for evidence of adverse effects such as nausea, vomiting, diarrhea, or abdominal pain.', NULL, 'The patient should be monitored for evidence of adverse effects such as nausea, vomiting, diarrhea, or abdominal pain. Ranitidine, nizatidine and famotidine do not inhibit CYP450 and would not be expected to affect erythromycin levels at recommended doses.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
