    ) VALUES (1389196, 'Fedratinib', 'Cimetidine', 'Moderate', 'Coadministration with dual CYP450 3A4 and 2C19 inhibitors may increase the plasma concentrations of fedratinib, which is a substrate of these isoenzymes.', NULL, 'According to the product labeling, concomitant use of fedratinib with dual CYP450 3A4 and 2C19 inhibitors should be avoided.', NULL, 'According to the product labeling, concomitant use of fedratinib with dual CYP450 3A4 and 2C19 inhibitors should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
