        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389226, 'Iron protein succinylate', 'Cimetidine', 'Minor', 'Limited data have suggested that concurrent administration of cimetidine may reduce the bioavailability of orally administered iron. Some investigators have suggested that cimetidine-induced increases in gastric pH may reduce the solubility of iron salts, which leads to decreased absorption. Based on existing evidence, no intervention (e.g., separating times of administration) appears to be necessary during concomitant use of cimetidine and iron products unless an interaction is suspected.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
